import React, { useState, useEffect } from 'react';
import {View, Alert} from "@vkontakte/vkui";
import bridge from '@vkontakte/vk-bridge';
import '@vkontakte/vkui/dist/vkui.css';
import Start from "./panels/Start";
import SelectType from "./panels/SelectType";
import "./App.css";
import Form from "./panels/Form";
import Additional from "./panels/Additional";
import Post from "./panels/Post";
import Wall from "./panels/Wall";
import Show from "./panels/Show";

import NewPodcast from "./panels/NewPodcast";
import {getState, setState} from "./state";

const App = () => {
	const [activePanel, setActivePanel] = useState("start");
	const [popout, setPopout] = useState(null);

	const changePanel = (p) => {
		setActivePanel(p);
		window.history.pushState({panel: p}, "");
	};

	const help = () => {
		setState({donated: getState().donated - (-100)});

		if (bridge && bridge.supports("VKWebAppShowStoryBox") && bridge.isWebView()) {
			setPopout(<Alert
				actionsLayout="vertical"
				actions={[{
					title: 'Рассказать друзьям',
					autoclose: true,
					action: () => story(),
				}, {
					title: 'Закрыть',
					autoclose: true,
					mode: 'cancel'
				}]}
				onClose={() => setPopout(null)}
			>
				<h2>Пожертвование</h2>
				<p>Спасибо, что пожертвовали деньги! Хотите рассказать об этом друзьям в Историях?</p>
			</Alert>);
		}
	};

	const story = () => {
		setPopout(null);

		const data = {
			background_type: 'none',
			stickers: [
				{
					sticker_type: 'renderable',
					sticker: {
						content_type: 'image',
						blob: "data:image/png;base64,",
						transform: {
							relation_width: 250 / document.documentElement.clientWidth,
							rotation: -10,
							translation_x: 0,
							translation_y: 0.3,
							gravity: 'center',
						},
						can_delete: true,
					},
				},
			],
		};

		bridge.send("VKWebAppShowStoryBox", data);
	};

	useEffect(() => {
		window.history.pushState({panel: "start"}, "");
		window.onpopstate = function(e) {
			setActivePanel((e.state && e.state.panel) || "start")
		};

		bridge.subscribe(({ detail: { type, data }}) => {
			if (type === 'VKWebAppUpdateConfig') {
				const schemeAttribute = document.createAttribute('scheme');
				schemeAttribute.value = data.scheme ? data.scheme : 'client_light';
				document.body.attributes.setNamedItem(schemeAttribute);
			}
		});
	}, []);

	return (
		<View activePanel={activePanel} popout={popout}>
			<Start id="start" go={changePanel}/>
			<NewPodcast id="newPodcast" go={changePanel}/>
			<Form id="form" go={changePanel}/>
			<Additional id="additional" go={changePanel}/>
			<Post id="post" go={changePanel}/>
			<Wall id="wall" go={changePanel} help={help}/>
			<Show id="show" help={help}/>
		</View>
	);
}

export default App;

